mapper.select:
select aaa.* from ( select max(rownum) over() totalcount, type_code, type_code as type_code_hidden, type_code_name, type_code_comt from ( select row_number() over() as rownum, type_code, type_code_name, type_code_comt from tb_comn_class
( type_code like CONCAT(#{paramStr1}, '%') OR type_code_name like CONCAT(#{paramStr1}, '%') )
) a group by type_code, type_code_name, type_code_comt ) aaa
LIMIT #{startRowNum}, #{perPage}

mapper.insert:
insert into tb_comn_class
 (
 type_code,
 type_code_name,
 type_code_comt,
 first_reg_dt,
 first_reg_user_id,
 last_upd_dt,
 last_upd_user_id
 )
 values
 (
 #{type_code}
 ,#{type_code_name}
 ,#{type_code_comt}
 ,sysdate()
 ,#{id}
 ,sysdate()
 ,#{id}
 )

mapper.update:
update tb_comn_class
set type_code_name = #{type_code_name}
   ,type_code_comt = #{type_code_comt}
   ,last_upd_dt = sysdate()
where type_code = #{type_code}

mapper.select:
select aaa.* from ( select max(rownum) over() totalcount, type_code, type_code as type_code_hidden, comn_code, comn_code_name, comn_code_comt, attr_01, attr_02, attr_03, attr_04, attr_05, use_yn from ( select row_number() over() as rownum, type_code, comn_code, comn_code_name, comn_code_comt, attr_01, attr_02, attr_03, attr_04, attr_05, use_yn from tb_comn_code
type_code = #{paramStr1}
use_yn = #{paramStr2}
) a group by type_code, comn_code, comn_code_name, comn_code_comt, use_yn,attr_01, attr_02, attr_03, attr_04, attr_05, use_yn ) aaa
LIMIT #{startRowNum}, #{perPage}

mapper.insert:
insert into tb_comn_code
(
    type_code,
    comn_code,
    comn_code_name,
    comn_code_comt,
    attr_01,
    attr_02,
    attr_03,
    attr_04,
    attr_05,
    use_yn,
    first_reg_dt,
    first_reg_user_id,
    last_upd_dt,
    last_upd_user_id
)
values
(
      #{type_code}
    , #{comn_code}
    , #{comn_code_name}
    , #{comn_code_comt}
    , #{attr_01}
    , #{attr_02}
    , #{attr_03}
    , #{attr_04}
    , #{attr_05}
    , #{use_yn}
    , sysdate()
    , #{id}
    , sysdate()
    , #{id}
)

mapper.update:
update tb_comn_code
set
      comn_code_name = #{comn_code_name}
    , comn_code_comt = #{comn_code_comt}
    , attr_01 = #{attr_01}
    , attr_02 = #{attr_02}
    , attr_03 = #{attr_03}
    , attr_04 = #{attr_04}
    , attr_05 = #{attr_05}
    , use_yn =  #{use_yn}
    , last_upd_dt = sysdate()
    , last_upd_user_id = #{id}
where type_code = #{type_code}
  and comn_code = #{comn_code}

update tb_comn_code
set
      use_yn = 'N'
    , last_upd_dt = sysdate()
    , last_upd_user_id = #{id}
where type_code = #{type_code}
and comn_code = #{comn_code}

mapper.select:
select
    aaa.*
from (
    select
        max(rownum) over() totalcount,
        rownum,
        char_id,
        char_id_hidden,
        char_name,
        class,
        atk_type,
        cq_use_yn,
        char_comt
    from (
        select
            row_number() over() as rownum,
            char_id,
            char_id as char_id_hidden,
            char_name,
            class,
            atk_type,
            cq_use_yn,
            char_comt
        from tb_comn_char


                class = #{paramStr1}


                cq_use_yn = #{paramStr2}


    ) a
    group by char_id, char_name, class, atk_type, cq_use_yn, char_comt

) aaa



        LIMIT  #{startRowNum}, #{perPage}

mapper.insert:
insert into tb_comn_char
(
    char_id,
    char_name,
    class,
    atk_type,
    cq_use_yn,
    char_comt,
    first_reg_dt,
    first_reg_user_id,
    last_upd_dt,
    last_upd_user_id
)
values
(
      #{char_id}
    , #{char_name}
    , #{class}
    , #{atk_type}
    , #{cq_use_yn}
    , #{char_comt}
    , sysdate()
    , #{id}
    , sysdate()
    , #{id}
)

mapper.update:
update tb_comn_char
set
     char_name         = #{char_name}
    ,class             = #{class}
    ,atk_type          = #{atk_type}
    ,cq_use_yn         = #{cq_use_yn}
    ,char_comt         = #{char_comt}
    ,last_upd_dt       = sysdate()
    ,last_upd_user_id  = #{id}
where char_id = #{char_id}

update tb_comn_char
set
      cq_use_yn = 'N'
    , last_upd_dt = sysdate()
    , last_upd_user_id = #{id}
where char_id = #{char_id}

mapper.select:
select aaa.* from ( select max(rownum) over() totalcount, rownum, item_id, item_name, char_id, item_type, cq_use_yn, icon_file, icon_x, icon_y, item_comt from ( select row_number() over() as rownum, item_id, item_name, char_id, item_type, cq_use_yn, icon_file, icon_x, icon_y, item_comt from tb_comn_item
item_type = #{paramStr1}
cq_use_yn = #{paramStr2}
order by item_id ) a group by item_id, item_name, item_type, cq_use_yn, item_comt, char_id, icon_file, icon_x , icon_y
) aaa order by item_id
LIMIT #{startRowNum}, #{perPage}

mapper.insert:
insert into tb_comn_item
(
item_id,
item_type,
item_name,
char_id,
item_comt,
cq_use_yn,
icon_file,
icon_x,
icon_y,
first_reg_dt,
first_reg_user_id,
last_upd_dt,
last_upd_user_id
)
values
(
  (SELECT nvl(MAX(a.item_id),0) + 1 FROM tb_comn_item a)
, #{item_type}
, #{item_name}
, #{char_id}
, #{item_comt}
, #{cq_use_yn}
, #{icon_file}
, #{icon_x}
, #{icon_y}
, sysdate()
, #{id}
, sysdate()
, #{id}
)

mapper.update:
update tb_comn_item
set
item_name          = #{item_name}
,item_type         = #{item_type}
,char_id           = #{char_id}
,item_comt         = #{item_comt}
,cq_use_yn         = #{cq_use_yn}
,icon_file         = #{icon_file}
,icon_x            = #{icon_x}
,icon_y            = #{icon_y}
,last_upd_dt       = sysdate()
,last_upd_user_id  = #{id}
where item_id = #{item_id}

update tb_comn_item
set
cq_use_yn = 'N'
, last_upd_dt = sysdate()
, last_upd_user_id = #{id}
where item_id = #{item_id}
